import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from "react-router-dom";


const RegisterUser = () => {
  const [gender, setGender] = useState('');
  const [age, setAge] = useState('');
  const [hobbies, setHobbies] = useState('');
  const [interests, setInterests] = useState('');
  const [message, setMessage] = useState('');
  const [redirect, setRedirect] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    const userData = {
      gender: gender,
      age: age,
      hobbies: hobbies,
      interests: interests
    };
    axios.post('http://localhost:5000/user', userData)
      .then(response => {
        setMessage('User registration successful.');
        setRedirect(true); // リダイレクトの状態を更新
        console.log(response);
      })
      .catch(error => {
        setMessage('User registration failed. Please try again later.');
        console.error('Error saving user data: ', error);
      });
  };

  const handleClick = () => {
    const history = useHistory();
    // リダイレクトを実行
    history.push('/confirmation-register');
  };

  
  return (
    <div>
    <h1>User Registration Form</h1>
    <form onSubmit={handleSubmit}>
      <label>
        Gender:
        <select value={gender} onChange={(e) => setHobbies(e.target.value)}>
        <option value="">Select a gender...</option>
        <option value="sports">male</option>
        <option value="game">female</option>
        <option value="game">no gender</option>
        </select>
      </label>
      <label>
        Age:
        <input type="text" value={age} onChange={(e) => setAge(e.target.value)} />
      </label>
      <label>
        Hobbies:
        <select value={hobbies} onChange={(e) => setHobbies(e.target.value)}>
          <option value="">Select a hobby...</option>
          <option value="sports">Sports</option>
          <option value="game">Game</option>
          <option value="picture">Picture</option>
          <option value="vacation">Vacation</option>
          <option value="swim">Swim</option>
          <option value="mount">Mount</option>
        </select>
      </label>
      <label>
        Interests:
        <input type="text" value={interests} onChange={(e) => setInterests(e.target.value)} />
      </label>
      <button type="submit" onClick={handleClick}>Submit</button>
    </form>
    {message && <p>{message}</p>}
    </div>
  );
};

export default RegisterUser;
