// src/App.js

import React, { useState, useEffect } from 'react';
import Home from './components/Home';
import RegisterUserInfo from './components/RegisterUserInfo';
import UserList from './components/UserList';
import SaearchUser from './components/SearchUser';
import ConfirmRegister from './components/ConfirmRegister';
import DeleteUser from './components/DeleteUser';
import DeleteAllUser from './components/DeleteAllUser';
import RegisterUser from './components/RegisterUser';
import Login from './components/Login';
import Logout from './components/Logout';

import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import Cookies from 'js-cookie'; // js-cookieライブラリのインポート

import Layout from './layout/Layout'; // Layoutコンポーネントのインポート



function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    // クッキーからログイン状態を取得
    const token = Cookies.get('token');
    const user_uuid = Cookies.get('user_uuid');
    const username = Cookies.get('username');

    if (token && user_uuid && username) {
      setIsLoggedIn(true);
      setUsername(username);
    } else {
      setIsLoggedIn(false);
      setUsername('');
    }
  }, []);
  return (
    <Router>
      {/* Layoutコンポーネントで全体のレイアウトを定義 */}
      <Layout isLoggedIn={isLoggedIn} username={username}>
      <div>
        <nav>
          <ul>
            {/* <li><Link to="/">ホーム</Link></li>
            <li><Link to="/registeruserinfo">register user info</Link></li>
            <li><Link to="/alluser">alluser</Link></li>
            <li><Link to="/searchuser">search user</Link></li>
            <li><Link to="/deleteuser">delete user</Link></li>
            <li><Link to="/deletealluser">all delete user</Link></li>
            <li><Link to="/registeruser">register user</Link></li>
            <li><Link to="/login">login user</Link></li>
            <li><Link to="/logout">logout user</Link></li> */}

            {/* ログイン状態に応じて表示するリンクを切り替え */}

          </ul>
          {/* {isLoggedIn ? <p>you are logged in </p> : <p>not logged in</p>}  */}
        </nav>

        {/* 各ページへのルート設定 */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/registeruserinfo" element={<RegisterUserInfo />} />
          <Route path="/alluser" element={<UserList />} />
          <Route path="/searchuser" element={<SaearchUser />} />
          <Route path="/confirmation-register" element={<ConfirmRegister />} />
          <Route path="/deleteuser" element={<DeleteUser />} />
          <Route path="/deletealluser" element={<DeleteAllUser />} />
          <Route path="/registeruser" element={<RegisterUser />} />
          <Route path="/login" element={<Login />} />
          <Route path="/logout" element={<Logout />} /> 
        </Routes>
      </div>
      </Layout>
    </Router>
  );
}

export default App;
