import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";


const PostUserInfo = () => {
  const [userinfo, setUserInfo] = useState('');
  const [gender, setGender] = useState('');
  const [age, setAge] = useState('');
  const [hobbies, setHobbies] = useState('');
  const [interests, setInterests] = useState('');
  const [message, setMessage] = useState('');
  const [redirect, SetRedirect] = useState(false);
  const navigation = useNavigate();

  useEffect(() => {
    // ユーザー情報を取得する関数を定義
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:5000/user_info', {
          headers: {
            'Authorization': `Bearer ${userUUID}`  // ヘッダーにuser_uuidを含める
          }
        });
        console.log(response);
        setUserInfo(response.data);
      } catch (error) {
        console.error('Error fetching user info: ', error);
      }
    };

    // ページが読み込まれたときにユーザー情報を取得
    fetchUserInfo();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const userData = {
      gender: gender,
      age: age,
      hobbies: hobbies,
      interests: interests
    };
    axios.post('http://localhost:5000/postuserinfo', userData)
      .then(response => {
        setMessage('User registration successful.');
        console.log("post success:",response);
        SetRedirect(true);
      })
      .catch(error => {
        setMessage('User registration failed. Please try again later.');
        console.error('Error saving user data: ', error);
      });
  };


  
  if (redirect) {
    navigation('/confirmation-register',{state: {message: message}});
  }
  
  return (
    <div>
    <h1>User Registration Form</h1>
    <form onSubmit={handleSubmit}>
      <label>
        Gender:
        <select value={gender} onChange={(e) => setGender(e.target.value)}>
        <option value="">Select a gender...</option>
        <option value="male">male</option>
        <option value="female">female</option>
        <option value="nogender">no gender</option>
        </select>
      </label>
      <label>
        Age:
        <input type="number" min="18" max="150" value={age} onChange={(e) => setAge(e.target.value)} />
      </label>
      <label>
        Hobbies:
        <select value={hobbies} onChange={(e) => setHobbies(e.target.value)}>
          <option value="">Select a hobby...</option>
          <option value="sports">Sports</option>
          <option value="game">Game</option>
          <option value="picture">Picture</option>
          <option value="vacation">Vacation</option>
          <option value="swim">Swim</option>
          <option value="mount">Mount</option>
        </select>
      </label>
      <label>
        Interests:
        <input type="text" value={interests} onChange={(e) => setInterests(e.target.value)} />
      </label>
      <button type="submit">Submit</button>
    </form>
    {message && <p>{message}</p>}
    </div>
  );
};

export default PostUserInfo;
