// src/components/UserForm.js

import React, { useState } from 'react';
import axios from 'axios';

const UserForm = () => {
  const [userData, setUserData] = useState({
    gender: '',
    age: '',
    hobbies: '',
    interests: ''
  });

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/user', userData)
      .then(response => {
        console.log(response.data);
        // ユーザーが正常に保存された場合の処理を追加する
      })
      .catch(error => {
        console.error('Error saving user data: ', error);
        // エラーが発生した場合の処理を追加する
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Gender:
        <input type="text" name="gender" value={userData.gender} onChange={handleChange} />
      </label>
      <label>
        Age:
        <input type="text" name="age" value={userData.age} onChange={handleChange} />
      </label>
      <label>
        Hobbies:
        <select value={hobbies} onChange={(e) => setHobbies(e.target.value)}>
          <option value="">Select a hobby...</option>
          <option value="sports">Sports</option>
          <option value="game">Game</option>
          <option value="picture">Picture</option>
          <option value="vacation">Vacation</option>
          <option value="swim">Swim</option>
          <option value="mount">Mount</option>
        </select>
      </label>
      <label>
        Interests:
        <input type="text" name="interests" value={userData.interests} onChange={handleChange} />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default UserForm;
