  // location.stateからisLoggedInとusernameを取得
  const { isLoggedIn, username } = location.state || {};

  const [userinfo, setUserInfo] = useState([]);
  const userUUID = Cookies.get('user_uuid');
  const [message, setMessage] = useState('');




