  //     })
  //     .catch(error => {
  //       console.error('Error checking favorite:', error);
  //     });
  // };
  const data = {
    user_uuid: userUUID,          // ログイン中のユーザーのID
    target_user_uuid: 'user2'    // お気に入りを確認したい相手のID
  };
  axios.get('http://localhost:5000/matching', userUUID)
    .then(response => {
      setIsFavorite(response.data.is_favorite);
    })
    .catch(error => {
      console.error('Error checking favorite:', error);
    });

  return (
    <div>
      {/* <button onClick={checkFavorite}>Check Favorite</button> */}
      {isFavorite === null ? null : (
        <p>{isFavorite ? 'You have favorited this user.' : 'You have not favorited this user.'}</p>
      )}
    </div>
  );
};

export default Matching;
